mapper.insert:
	  
	    INSERT INTO tblloaner(
              user_id
            , loaner_name
            , id_card
            , gender
            , phone
            , sts
            , txt           
            , modify_by
            , modify_date
            , action
            , address)
       VALUES (
       		   #{user_id}
       		 , #{loaner_name}
       		 , #{id_card}
       		 , #{gender} 
       		 , #{phone}
       		 , #{sts}
       		 , #{txt}
       		 , #{modify_by}
       		 , #{modify_date}
       		 , #{action}
       		 , #{address}
       		 );
         
       		 SELECT CAST(CURRVAL('tblloaner_loaner_id_seq') as INTEGER)

mapper.update:
		UPDATE tblloaner
  		   SET   
  		         user_id=#{user_id}
  		       , loaner_name=#{loaner_name}
  		       , id_card=#{id_card}
  		       , gender=#{gender}
  		       , phone=#{phone}
  		       , sts=#{sts}
  		       , txt=#{txt}
  		       , modify_date=#{modify_date}
  		       , modify_by=#{modify_by}
  		       , action=#{action}
  		       , address=#{address}
        WHERE loaner_id = #{loaner_id}
