mapper.insert:
	    INSERT INTO tblloan(
             loaner_id
           , user_id
           , start_date
           , count_day
           , total_money
           , rate
           , type_payment
           , time
           , decrement
           , type_money
           , sts
           , txt
           , modify_date
           , modify_by
           , action)
        VALUES (
             #{loaner_id}
           , #{user_id}
           , #{start_date}
           , #{count_day}
           , #{total_money}
           , #{rate}
           , #{type_payment}
           , #{time}
           , #{decrement}
           , #{type_money}
           , #{sts}
           , #{txt}
           , #{modify_date}
           , #{modify_by}
           , #{action}
           	);
	    
	         SELECT CAST(CURRVAL('tblloan_loan_id_seq') as INTEGER)

	INSERT INTO tblloan_payment(
              loan_id
            , payment_date
            , prak_derm
            , total_rate
            , total_left
            , txt
            , modify_date
            , modify_by
            , action
            , note)
    VALUES (
    		  #{loan_id}
    		, #{payment_date}
    		, #{prak_derm}
    		, #{total_rate}
    		, #{total_left}
    		, #{txt}
    		, #{modify_date}
    		, #{modify_by}
    		, #{action}
    		, #{note}
    		);

mapper.select:
select count(*) as tt from tblloan tl inner join tblloaner tln on tln.loaner_id=tl.loaner_id where tl.sts != '9'
AND ( cast(loan_id as varchar) like '%'|| #{search} ||'%' or cast(total_money as varchar) like '%'|| #{search} ||'%' or tln.loaner_name like '%'|| #{search} ||'%' )
AND start_date >= #{startDate} AND start_date <= #{endDate}
AND type_payment = #{typePayment}
AND decrement = 0
AND decrement > 0

mapper.update:
UPDATE tblloan
   SET   
   	     user_id     = #{user_id}
   	   , start_date  = #{start_date}
   	   , total_money = #{total_money}
   	   , rate        = #{rate}
   	   , type_payment= #{type_payment}
   	   , "time"      = #{time}
   	   , decrement   = #{decrement}
   	   , type_money  = #{type_money}
   	   , sts         = #{sts}
   	   , txt         = #{txt}
   	   , modify_date = #{modify_date}
   	   , modify_by   = #{modify_by}
   	   , action      = #{action}
 WHERE loan_id = cast(#{loan_id} as int)
 
 	 and loaner_id = cast(#{loaner_id} as int)

UPDATE tblloan_payment
   SET  txt         = #{txt}
       , modify_date= #{modify_date}
       , modify_by  = cast(#{modify_by} as int)
       , action     = #{action}
       , note       = #{note}
 WHERE payment_id=cast(#{payment_id} as int)

UPDATE tblloan_payment
   SET  txt         = #{txt}
       , modify_date= #{modify_date}
       , modify_by  = cast(#{modify_by} as int)
       , action     = #{action}
       , note       = #{note}
 WHERE txt='1' and loan_id = cast(#{loan_id} as int)
