mapper.insert:
  		INSERT INTO public.tbluser(
	              user_code
	            , full_name
	            , gender
	            , phone
	            , email
	            , address
	            , username
	            , password
	            , sts
	            , txt
	            , photo
	            , modify_by
	            , modify_date
	            , action
	            )
	    VALUES (
	    		  #{user_code}
	    		, #{full_name}
	    		, #{gender}
	    		, #{phone}
	    		, #{email}
	    		, #{address}
	    		, #{username}
	    		, #{password}
	    		, #{sts}
	    		, #{txt}
	    		, #{photo}
	    		, cast(#{modify_by} as int)
	    		, #{modify_date}
	    		, #{action}
	    		
	    		);

mapper.update:
  		UPDATE tbluser
		   SET 
		   	     user_code  = #{user_code}
		   	   , full_name  = #{full_name}
		   	   , gender     = #{gender}
		   	   , phone      = #{phone}
		   	   , email      = #{email}
		   	   , address    = #{address}
		   	   , username   = #{username}
		   	   , password   = #{password}
		   	   , sts        = #{sts}
		   	   , txt        = #{txt}
		   	   , photo      = #{photo}
		   	   , modify_by  = #{modify_by}
		   	   , modify_date= #{modify_date}
		   	   , action     = #{action}
		 WHERE user_id = #{user_id};

mapper.select:
SELECT user_id , user_code , full_name , gender , phone , email , address , username , password , modify_by , modify_date , photo , sts , txt FROM tbluser WHERE 1=1 AND sts != '9'
AND user_id = cast(#{userId} as int)
AND username = #{userName}
AND password = #{password}
